    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int a = 5000;
        int b = 50000;
        int c = 100000;
        if(CB1.getSelectedIndex()==1){
            jTextField1.setText("Harga Barang");
            jTextField4.setText("Rp."+a);
            jTextField2.setText("Diskon");
            jTextField5.setText("0%");
            jTextField3.setText("Stock");
            jTextField6.setText("500");
       }
       else if(CB1.getSelectedIndex()==2){
            jTextField1.setText("Harga Barang");
            jTextField4.setText("Rp."+b);
            jTextField2.setText("Diskon");
            jTextField5.setText("10%");
            jTextField3.setText("Stock");
            jTextField6.setText("100");
       }
       else if(CB1.getSelectedIndex()==3){
            jTextField1.setText("Harga Barang");
            jTextField4.setText("Rp."+c);
            jTextField2.setText("Diskon");
            jTextField5.setText("20%");
            jTextField3.setText("Stock");
            jTextField6.setText("100");
       }
        
    }//GEN-LAST:event_jButton1ActionPerformed

