    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int a = 5000;
        int b = 50000;
        int c = 100000;
        if(CB1.getSelectedIndex()==1){
            if(CB2.getSelectedIndex()==0){
                jTextField7.setText("Total yang harus anda bayar = Rp."+a);
            }
            else if(CB2.getSelectedIndex()==1){
                jTextField7.setText("Total yang harus anda bayar = Rp."+2*a);
            }
            else if(CB2.getSelectedIndex()==2){
                jTextField7.setText("Total yang harus anda bayar = Rp."+3*a);
            }
            else if(CB2.getSelectedIndex()==3){
                jTextField7.setText("Total yang harus anda bayar = Rp."+4*a);
            }
            else if(CB2.getSelectedIndex()==4){
                jTextField7.setText("Total yang harus anda bayar = Rp."+5*a);
            }   
            else if(CB2.getSelectedIndex()==5){
                jTextField7.setText("Total yang harus anda bayar = Rp."+6*a);
            }   
        }
        else if(CB1.getSelectedIndex()==2){
            if(CB2.getSelectedIndex()==0){
                jTextField7.setText("Total yang harus anda bayar = Rp."+(b-(b*10/100)));
            }
            else if(CB2.getSelectedIndex()==1){
                jTextField7.setText("Total yang harus anda bayar = Rp."+2*(b-(b*10/100)));
            }
            else if(CB2.getSelectedIndex()==2){
                jTextField7.setText("Total yang harus anda bayar = Rp."+3*(b-(b*10/100)));
            }
            else if(CB2.getSelectedIndex()==3){
                jTextField7.setText("Total yang harus anda bayar = Rp."+4*(b-(b*10/100)));
            }
            else if(CB2.getSelectedIndex()==4){
                jTextField7.setText("Total yang harus anda bayar = Rp."+5*(b-(b*10/100)));
            }   
            else if(CB2.getSelectedIndex()==5){
                jTextField7.setText("Total yang harus anda bayar = Rp."+6*(b-(b*10/100)));
            }
        }
        else if(CB1.getSelectedIndex()==3){
            if(CB2.getSelectedIndex()==0){
                jTextField7.setText("Total yang harus anda bayar = Rp."+(c-(c*20/100)));
            }
            else if(CB2.getSelectedIndex()==1){
                jTextField7.setText("Total yang harus anda bayar = Rp."+2*(c-(c*20/100)));
            }
            else if(CB2.getSelectedIndex()==2){
                jTextField7.setText("Total yang harus anda bayar = Rp."+3*(c-(c*20/100)));
            }
            else if(CB2.getSelectedIndex()==3){
                jTextField7.setText("Total yang harus anda bayar = Rp."+4*(c-(c*20/100)));
            }
            else if(CB2.getSelectedIndex()==4){
                jTextField7.setText("Total yang harus anda bayar = Rp."+5*(c-(c*20/100)));
            } 
            else if(CB2.getSelectedIndex()==5){
                jTextField7.setText("Total yang harus anda bayar = Rp."+6*(c-(c*20/100)));
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

